import React from "react";
import classes from "./Backdrop.module.css";

const backdrop = (props) => {
    return (

        props.show ? <div className={classes.Backdrop} onClick={props.clicked}>{props.children}</div> : <div>{props.children}</div>
    );
} 

export default backdrop;
